mapper.select:
SELECT ID
FROM EMPLOYEE
WHERE USER_ID = #{sessionId}

mapper.update:
UPDATE PROJECT
SET COLOR = #{color}
WHERE ID = #{id}

mapper.select:
 SELECT ID
 FROM FAVORITES
	WHERE PROJECT_ID = #{projectId} AND EMPLOYEE_ID = #{employeeId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE CREATED_AT >= SYSDATE - INTERVAL '7' DAY AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE STATUS = 'To Do' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE STATUS = 'In Progress' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE STATUS = 'Done' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE STATUS = 'Resolved' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE PRIORITY = 'critical' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE PRIORITY = 'high' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE PRIORITY = 'middle' AND PROJECT_ID = #{projectId}

	SELECT COUNT(*)
    FROM ISSUE
    WHERE PRIORITY = 'low' AND PROJECT_ID = #{projectId}

mapper.update:
update project
set color=#{color}, title = #{title}, subtitle = #{subtitle}, modifier = #{sessionId}
where id = #{projectId}

mapper.select:
SELECT rs.TABLE_NAME, rs.CHANGE_TYPE, u.NAME as MASTER, update_rs.NEW_DATA AS UPDATE_DATA
FROM RECENT_STATUS rs
JOIN USERS u ON TRIM(SUBSTR(rs.CREATOR_MODIFIER, INSTR(rs.CREATOR_MODIFIER, '-') + 2)) = TO_CHAR(u.ID)
LEFT JOIN (
    SELECT ID, NEW_DATA
    FROM RECENT_STATUS
    WHERE TABLE_NAME = 'PROJECT' AND CHANGE_TYPE = 'UPDATE' AND PROJECT_ID = #{project_id}
) update_rs ON rs.ID = update_rs.ID
WHERE rs.PROJECT_ID = #{project_id}
