mapper.update:
update user
set name =
#{name}, age = #{age},
gender = #{gender}, email = #{email}
where id =
#{id}

mapper.select:
select *
from (select rownum rnum, b.*
from (select *
from user
where id != 'admin'

order by id
) b
where rownum <= #{end}
)
where rnum between #{start} and #{end}

sql.if:
and
id name age gender
like #{search_word}
